import React, { useState } from 'react';
import Scroll from './Scroll';
import SearchList from './SearchList';
import { useEffect, useRef} from "react"
let skills = [
  "Angular",
  "CSS",
  "Graphic Design",
  "Ember",
  "HTML",
  "Information Architecture",
  "JavaScript",
  "Mechanical Engineering",
  "Meteor",
  "NodeJS",
  "Plumbing",
  "Python",
  "Rails",
  "React",
  "Kitchen Repair",
  "Ruby",
  "UI Design",
  "User Experience",
]

function Search({ details }) {
  var filterData = [
    { name: 'Matthew', sex: 'male' },
    { name: 'Amanda', sex: 'female' }
   ];
  var FilterForm = React.createClass({
    getInitialState: function() {
      return {
        data: this.props.data,
        sex: ''
      }
    },
    handleChange: function(val) {
      this.setState({sex: val});
      var filteredData = this.props.data.filter(function(item) {
        return item.sex === val;
      });
      this.setState({data: filteredData});
      console.log(filteredData, val);
    },
    render: function() {
      return ( 
        <div className="filter-form">
          <h1>Filter Form</h1>
          <FilterOptions data={this.state.data} changeOption={this.handleChange} /> 
          <FilterItems data={this.state.data} />
        </div>
      );
    }
  });
  var FilterOptions = React.createClass({
    getInitialState: function() {
      return {
        data: this.props.data,
        sex: ''
      }
    },
    handleChange: function(e) {
      var val = e.target.value;
      this.setState({sex: val});
      this.props.changeOption(val);
    },
    render: function() {
      return ( 
        <select id="sex" value={this.state.sex} onChange={this.handleChange}>
          <option value=""></option>
          <option value="male">male</option>
          <option value="female">female</option> 
        </select>	
      );
    }
  });
  var FilterItems = React.createClass({
    render: function() {
      return (
        <div className="filter-item">
          {this.props.data.map(function(item) {
            return ( 
              <div>{item.name}</div>
            );
          })}
        </div>
      );
    }
  });

  const [searchField, setSearchField] = useState("");
  const [selectedSkill, setSelectedSkill] = useState("")
  const [dropdownSearchValue, setDropdownSearchValue] = useState("")
  const [editMode, setEditMode] = useState(false)
  const dropdownRef = useRef()

  useEffect(() => {
    const checkIfClickedOutside = e => {
      // If the menu is open and the clicked target is not within the menu,
      // then close the menu
      if (
        editMode &&
        dropdownRef.current &&
        !dropdownRef.current.contains(e.target)
      ) {
        setEditMode(false)
      }
    }
    document.addEventListener("mousedown", checkIfClickedOutside)
    return () => {
      // Cleanup the event listener
      document.removeEventListener("mousedown", checkIfClickedOutside)
    }
  }, [editMode])

  const skillSelectionHandler = skill => {
    setSelectedSkill(skill)
    setDropdownSearchValue("")
    setEditMode(false)
  }

  const filteredSkills = skills.filter(skill =>
    skill.match(new RegExp(dropdownSearchValue, "i"))
  )

  const filteredPersons = details.filter(
    person => {
      if (typeof(person.first_name) === 'undefined' && typeof(person.last_name) === 'undefined') {
        return
      }
      else if (typeof(person.first_name) === 'undefined') {
        return (
          person
        .last_name
        .toLowerCase()
        .includes(searchField.toLowerCase())
        )
      }
      else{
        return (
          person
        .first_name
        .toLowerCase()
        .includes(searchField.toLowerCase())
        )
      }
    }
  );

const handleChange = e => {
  setSearchField(e.target.value);
};

  function searchList() {
    return (
      <Scroll>
        <SearchList filteredPersons={filteredPersons} />
      </Scroll>
    );
  }

  return (
    <section className="garamond">

    <div className="App">
      <h2>Dropdown filtering</h2>

      {editMode ? (
        // display the dropdown when the input us focused
        <div ref={dropdownRef} className="dropdown-wrapper">
          <input
            className="dropdown-input"
            name="dropdown-input"
            autoFocus
            onChange={e => setDropdownSearchValue(e.target.value)}
            value={dropdownSearchValue}
          />
          <div className="dropdown-list">
            <ul>
              {filteredSkills.map(skill => {
                return (
                  <li key={skill} onClick={() => skillSelectionHandler(skill)}>
                    {skill}{" "}
                  </li>
                )
              })}
              {filteredSkills.length === 0 && (
                <li className="no-result">No results found</li>
              )}
            </ul>
          </div>
        </div>
      ) : (
        <input
          // Grey out the text when "Select Primary skill" input hint is shown
          className={`dropdown-search ${
            !(dropdownSearchValue || selectedSkill) && "default"
          }`}
          onFocus={() => setEditMode(true)}
          // Display the selected skill or "Select Primary skill" input hint
          value={selectedSkill || "Select Primary skill"}
        />
      )}
    </div>
      <div className="navy georgia ma0 grow">
        <h2 className="f2">Search for an Expert!</h2>
      </div>
      <div className="pa2">
        <input 
          className="pa3 bb br3 grow b--none bg-lightest-blue ma3"
          type = "search" 
          placeholder = "Search People" 
          onChange = {handleChange}
        />
      </div>
      {searchList()}

    </section>

  );
}

export default Search;
