import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';

class Card extends React.Component {
  constructor(props) {
  super(props);
    this.person = props.person
   }

   handleClick() {
    window.location.href="https://www.youtube.com/watch?v=ihloYYx7qfY"
  };

 render() {
  return(
    <button onClick={() => this.handleClick()}>
    <div className="tc bg-light-blue dib br3 pa3 ma2 grow bw2 shadow-5">
    <img className="br-100 h3 w3 dib" alt={this.person.name} />
    <div> <Grid item xs={4}>
        <Item><h1>{this.person.first_name}</h1></Item>
      </Grid>
      <h1>{this.person.first_name}</h1>
      <h2>{this.person.last_name}</h2>
      <p>{this.person.email}</p>
      <p>{this.person.interest}</p>
    </div>
  </div>
  </button>
);
  }

 }
const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

function FormRow() {
  return (
    <React.Fragment>
      <Grid item xs={4}>
        <Item>Name</Item>
      </Grid>
    </React.Fragment>
  );
}

function NestedGrid() {
  return (
    <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={1}>
        <Grid container item spacing={3}>
          <FormRow />
        </Grid>
        <Grid container item spacing={3}>
          <FormRow />
        </Grid>
        <Grid container item spacing={3}>
          <FormRow />
        </Grid>
      </Grid>
    </Box>
  );
}




export default Card;
