import React, {
  useState
} from 'react';
import Scroll from './Scroll';
import SearchList from './SearchList';
import {
  useEffect,
  useRef
} from "react"

function Search({
  details
}) {

  const [searchField, setSearchField] = useState("");


  const filteredPersons = details.filter(
      person => {
          if (typeof(person.first_name) === 'undefined' && typeof(person.last_name) === 'undefined') {
              return
          } else if (typeof(person.first_name) === 'undefined') {
              return (
                  person
                  .last_name
                  .toLowerCase()
                  .includes(searchField.toLowerCase())
              )
          } else {
              return (
                  person
                  .first_name
                  .toLowerCase()
                  .includes(searchField.toLowerCase()) ||
                  person
                  .last_name
                  .toLowerCase()
                  .includes(searchField.toLowerCase()) ||
                  person
                  .geographic_areas
                  .toLowerCase()
                  .includes(searchField.toLowerCase())
              )
          }
      }
  );

  const handleChange = e => {
      console.log(e)
      setSearchField(e.target.value);

  };

  function searchList() {
      return ( <
          Scroll >
          <
          SearchList filteredPersons = {
              filteredPersons
          }
          /> <
          /Scroll>
      );
  }

  return ( <
      section className = "garamond" >
      <
      div className = "navy georgia ma0 grow" >
      <
      h2 className = "f2" > Name Filter < /h2> <
      /div> <
      div className = "pa2" >
      <
      input className = "pa3 bb br3 grow b--none bg-lightest-blue ma3"
      type = "search"
      placeholder = "Search People"
      onChange = {
          handleChange
      }
      /> <
      /div> <
      div className = "navy georgia ma0 grow" >
      <
      h2 className = "f2" > Geographic Filter < /h2> <
      /div> <
      div className = "pa2" >
      <
      input className = "pa3 bb br3 grow b--none bg-lightest-blue ma3"
      type = "search"
      placeholder = "Search People2"
      onChange = {
          handleChange
      }
      /> <
      /div> {
          searchList()
      }


      <
      /section>

  );
}

export default Search;
